if (document.readyState === "loading") {
  // DOM  ,  DOMContentLoaded
} else {
  // DOM  
  window.dynamicHeaderHeight = new DynamicHeaderHeight();
}

//       
if (typeof module !== "undefined" && module.exports) {
  module.exports = DynamicHeaderHeight;
}

